using System.Collections;using System.Collections.Generic;using UnityEngine;namespace FX{    public static class FxMath    {        /// <summary>        /// 二阶贝塞尔曲线插值        /// </summary>        /// <param name="t"></param>        /// <param name="p0"></param>        /// <param name="p1"></param>        /// <param name="p2"></param>        /// <returns></returns>        public static Vector3 CalculateQuadBezierPoint(float t, Vector3 p0, Vector3 p1, Vector3 p2)        {            float u = 1 - t;            float tt = t * t;            float uu = u * u;            Vector3 p = uu * p0;            p += 2 * u * t * p1;            p += tt * p2;            return p;        }        /// <summary>        /// 三阶贝塞尔曲线插值        /// </summary>        /// <param name="t"></param>        /// <param name="p0"></param>        /// <param name="p1"></param>        /// <param name="p2"></param>        /// <param name="p3"></param>        /// <returns></returns>        public static Vector3 CalculateCubicBezierPoint(float t, Vector3 p0, Vector3 p1, Vector3 p2, Vector3 p3)        {            float u = 1 - t;            float tt = t * t;            float uu = u * u;            float uuu = uu * u;            float ttt = tt * t;            Vector3 p = uuu * p0;            p += 3 * uu * t * p1;            p += 3 * u * tt * p2;            p += ttt * p3;            return p;        }    }}